import React, { useState } from 'react';
import ResumeUploader from './ResumeUploader';
import PastResumesTable from './PastResumesTable';
import ResumeDetails from './ResumeDetails';

export default function App(){
  const [tab, setTab] = useState('analyze');
  const [latestAnalysis, setLatestAnalysis] = useState(null);
  const [selectedResume, setSelectedResume] = useState(null);

  return (
    <div className="min-h-screen bg-gray-100 p-8">
      <div className="max-w-6xl mx-auto">
        <header className="mb-6">
          <h1 className="text-4xl font-bold text-gray-800">Resume Analyzer</h1>
          <p className="text-gray-600">AI-powered resume feedback to help you improve and upskill.</p>
        </header>

        <div className="bg-white rounded shadow p-6">
          <div className="flex gap-2 mb-6">
            <button onClick={()=>setTab('analyze')} className={`px-4 py-2 rounded ${tab==='analyze'?'bg-indigo-600 text-white':'bg-white border'}`}>Analyze</button>
            <button onClick={()=>setTab('history')} className={`px-4 py-2 rounded ${tab==='history'?'bg-indigo-600 text-white':'bg-white border'}`}>History</button>
          </div>

          {tab==='analyze' && (
            <>
              <ResumeUploader onResult={(a)=>setLatestAnalysis(a)} />
              {latestAnalysis && <div className="mt-6"><ResumeDetails analysis={latestAnalysis} /></div>}
            </>
          )}

          {tab==='history' && <PastResumesTable onSelect={(r)=>setSelectedResume(r)} /> }
        </div>

        {selectedResume && (
          <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center">
            <div className="bg-white p-6 rounded shadow-lg max-w-3xl w-full">
              <div className="flex justify-between items-center mb-4">
                <h2 className="text-xl font-semibold">Resume Details</h2>
                <button className="text-gray-500" onClick={()=>setSelectedResume(null)}>Close âœ•</button>
              </div>
              <ResumeDetails analysis={selectedResume.analysis} />
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
