import React, { useState } from 'react';
import axios from 'axios';

export default function ResumeUploader({ onResult }){
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);

  const submit = async (e) => {
    e.preventDefault();
    if(!file) return alert('Please choose a PDF file');
    const form = new FormData();
    form.append('resume', file);
    try {
      setLoading(true);
      const res = await axios.post('/api/resumes/upload', form, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      onResult(res.data.analysis);
    } catch (err) {
      console.error(err);
      alert(err.response?.data?.error || 'Upload failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={submit} className="space-y-4">
      <div className="grid grid-cols-6 gap-4 items-center">
        <label className="col-span-1 text-sm font-medium">Select PDF</label>
        <input className="col-span-5" type="file" accept="application/pdf" onChange={e=>setFile(e.target.files[0])} />
      </div>

      <div className="flex items-center gap-4">
        <button className="bg-indigo-600 text-white px-4 py-2 rounded" type="submit" disabled={loading}>
          {loading ? 'Analyzing...' : 'Upload & Analyze'}
        </button>
        <span className="text-sm text-gray-500">Max 8 MB. PDF only.</span>
      </div>
    </form>
  );
}
